HOME=os.environ['HOME']
WORKDIR=HOME+"/functionalAnnotation"
COMPUTEDIR=os.environ['GLOBAL']
HMM="./HMM"

    #################################                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
