    ##################################                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       
    #FILES                           #                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       
    ##################################                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       

#GENOME                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
GENOME=WORKDIR+"/{genome}.faa"
#GENOME=WORKDIR+"/{genome}.faa"                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              
GENOMES,=glob_wildcards(GENOME);


#PANNZERDB=HOME+"/share/database/pannzer/PANNZER_DB/Pannzer_dump.sql"                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
#PANNZERDIR=HOME+"/share/database/pannzer/PANNZER_DB/"                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       
#PANNZERUNIPROT=HOME+"/share/database/pannzer/PANNZER_DB/UNIPROTClean.tab"                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   


UNIREF=HOME+"/share/database/uniref90UniprotFungi.fasta"
CAZY=HOME+"/share/database/cazydb.fa"
MEROPS=HOME+"/share/database/merops.fa"
TCDB=HOME+"/share/database/tcdb.fa"
TCDBR=HOME+"/share/database/tcdb.dr"
DFVF=HOME+"/share/database/dfvf.fa"

#CEGMAKOGS                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   
CEGMAKOGS=HOME+"/bin/CEGMA_v2/data/kogs.fa"

#EVMWEIGHT                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   
