rule all:
     input: expand("./{genome}.{anal}", genome=GENOMES,anal=['blastSwissProt','smash','merged.tsv'])


##################################################################################                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##                                                                               #                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##      merge interproscan, merops, tcdb, etc... into  .merge.tsv                #                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##                                                                               #                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##################################################################################                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           


rule mergePredictions:
     input: merops="{genome}.merops", cazy="{genome}.cazy", tsv="{genome}.tsv", tcdb="{genome}.tcdb"
     output: "{genome}.merged.tsv"
     shell:"""                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               
     cat {input} > {output}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  
     """


##################################################################################                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##                                                                               #                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##                            antiSmash                                          #                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##                                                                               #                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##################################################################################                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           

rule antiSmash:
     input: annotation="{genome}.gff", genome="{genome}.fasta"
     output: "{genome}.smash"
     params: cluster="-cwd -V -l mem_free=8G -l h_vmem=8G"
     threads: THREADS
     shell:"""                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               
     prefix=`date --rfc-3339=ns  | md5sum | head -c 16`                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
     mkdir {COMPUTEDIR}/${{prefix}}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          
     cd {COMPUTEDIR}/${{prefix}}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             
     cat {WORKDIR}/{input.annotation} | grep CDS | sed -r 's/ID=//g' | sed -r 's/;Parent.+//g' | perl -lane '($start,$end)=($F[3]<$F[4]? ($F[3],$F[4]):($F[4],$F[3])); print "$F[0].fasta\t$F[8]\t$start\t$end\thypo"' | sort -k 2,2 > annotationtable.txt                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   
     cat {WORKDIR}/{input.genome} | perl -lane '$line=$F[0]; chomp($line); if($line=~/^>/){{$fileName=$line; $fileName=~s/>//g; $fileName.=".fasta"; `rm $fileName;`}} open(FILE,">>$fileName"); print FILE $line; close(FILE);'                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             
     ln -s ~/bin/antismash-2.1.1/format_embl.py .                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            
     python ./format_embl.py                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 
     parallel -j 8 " ~/bin/antismash-2.1.1/run_antismash {} -c 32 --outputfolder  {}.out" ::: *.embl                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         
     mkdir {WORKDIR}/{output}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                
     mv *.out {WORKDIR}/{output}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             
     """


##################################################################################                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##                                                                               #                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##                            DFVF                                               #                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##                                                                               #                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##################################################################################                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           

#rule DFVF:                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  
#     input: protein="{genome}.faa", dfvf=DFVF                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               
#     output: "{genome}.dfvf"                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                
#     params: cluster="-cwd -V -l mem_free=8G -l h_vmem=8G"                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  
#     threads: THREADS                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       
#     shell:"""                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              
#     blastp -db {input.dfvf} -query {input.protein} -num_threads {threads} -outfmt "7 qseqid qlen qstart qend sseqid slen sstart send pident nident evalue" > {output}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
#     """                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    



##################################################################################                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##                                                                               #                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##                            TCDB                                               #                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##                                                                               #                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##################################################################################                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           

#curl http://www.tcdb.org/seqfile/tcdb > tcdb.fa Sequence file                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               
#formatdb  -i tcdb.fa -p T -o T                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              
#curl http://www.tcdb.org/public/tcdb.dr > tcdb.fr Mapping file                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              



rule TCDB:
     input: protein="{genome}.faa", tcdb=TCDB
     output: "{genome}.tcdb"
     params: cluster="-cwd -V -l mem_free=8G -l h_vmem=8G"
     threads: THREADS
     shell:"""                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               
     #BLASTP                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 
     blastp -db {input.tcdb} -query {input.protein} -num_threads {threads} -evalue 1e-3 -outfmt "7 qseqid qlen qstart qend sseqid slen sstart send pident nident evalue" > {output}.temp                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     
     #Format and convert it to tsv format                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
     for i in `cut -f 1 {output}.temp | grep -v "#" | sort -u`; do                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
                    grep $i {output}.temp | grep -v "#" | sort -k 11,11g | sed -r 's/gnl\|TC-DB\|//g' | head -n1 | perl -lane 'my $id=$F[4]; my $newId =`grep $id {TCDBR}`; $newId=~s/.+TCDB;//g; $newId=~s/;.+//g; @temp=split(/\./,$newId); $newId=join("",@temp[0..2]); printf "$F[0]\tbla\t$F[1]\ttcdb\t$newId\t$F[2]\t$F[3]\t$F[10]\n"';                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                
     done | sort -u >  {output}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              
     """




##################################################################################                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##                                                                               #                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##                            MEROPS                                             #                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##                                                                               #                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##################################################################################                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           

#How to get merops database                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  
#wget ftp://ftp.sanger.ac.uk/pub/MEROPS/current_release/pepunit.lib                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          
#mv pepunit.lib ~/share/database/                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            
#cp pepunit.lib merops.fa                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
#mv merops.tmp merops.fa                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     
#formatdb -i merops.fa -p T -o T                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             

rule MEROPS:
     input: protein="{genome}.faa", merops=MEROPS
     output: "{genome}.merops"
     params: cluster="-cwd -V -l mem_free=8G -l h_vmem=8G"
     threads: THREADS
     shell:"""                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               
     #BLASTP                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 
     blastp -db {input.merops} -query {input.protein} -num_threads {threads} -evalue 1e-3 -outfmt "7 qseqid qlen qstart qend sseqid slen sstart send pident nident evalue" > {output}.temp                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   
     #For each gene get the hit with the lowest evalue / convert it to tsv format                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            
     for i in `cut -f 1 {output}.temp | grep -v "#" | sort -u`; do grep $i {output}.temp | grep -v "#" | sort -k 11,11g | head -n 1 | perl -lane 'my $id=$F[4]; $id=~s/[A-Z]$//;$id=~s/[^_]+_//; printf "$F[0]\tbla\t$F[1]\tMerops\t$id\t$F[2]\t$F[3]\t$F[10]\n"'; done | sort -u > {output};
     printf "\n" >> output
     """

##################################################################################                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##                                                                               #                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##                            CAZY                                               #                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##                                                                               #                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##################################################################################                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           

rule CAZY:
     input: protein="{genome}.faa", cazy=CAZY
     output: "{genome}.cazy"
     params: cluster="-cwd -V -l mem_free=8G -l h_vmem=8G"
     threads: THREADS
     shell:"""                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               
     #BLASTP                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 
     blastp -db {input.cazy} -query {input.protein} -num_threads {threads} -evalue 1e-3 -outfmt "7 qseqid qlen qstart qend sseqid slen sstart send pident nident evalue" > {output}.temp                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     
     #Format output                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          
     for i in `cut -f 1 {output}.temp | grep -v "#" | sort -u`; do grep $i {output}.temp | grep -v "#" | sort -k 11,11g | head -n 1 | perl -lane 'my $id=$F[4]; $id=~s/[A-Z]$//;$id=~s/[^_]+_//; printf "$F[0]\tbla\t$F[1]\tCazy\t$id\t$F[2]\t$F[3]\t$F[10]\n"'; done | sort -u > {output};                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  
     """


##################################################################################                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##                                                                               #                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##                            INTERPROSCAN                                       #                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##                                                                               #                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##################################################################################                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           


rule interproscan:
     input: protein="{genome}.faa"
     output: tsv="{genome}.tsv", dir="{genome}.interproscan.temp"
     params: cluster="-cwd -V -l mem_free=32G -l h_vmem=32G"
     threads: THREADS
     shell:"""                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               
     mkdir -p {WORKDIR}/{output.dir}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         
     cd {WORKDIR}/{output.dir}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               
     rm *                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
     split -l 50 -d {WORKDIR}/{input.protein} {wildcards.genome}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             
     #computation                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            
     parallel -j 85 'echo "cd ${{GLOBAL}}/interproscan-5.11-51.0 && ./interproscan.sh -i {WORKDIR}/{output.dir}/{{}} -f xml -goterms -pa -o {WORKDIR}/{output.dir}/{{}}.bla" | qsub -cwd -V -sync y' ::: {wildcards.genome}*                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 
     #conversion                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             
     echo '<?xml version="1.0" encoding="UTF-8" standalone="yes"?>' > all.xml                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                
     echo '<protein-matches xmlns="http://www.ebi.ac.uk/interpro/resources/schemas/interproscan5">' >> all.xml                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               
     cat -v *.bla | sed -r 's/\^\\//g' | grep -Pv "(<?xml|protein-matches)"  >> all.xml                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
     echo '</protein-matches>' >> all.xml                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
     ${{GLOBAL}}/interproscan-5.11-51.0/interproscan.sh -mode convert -i all.xml -f tsv                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
     mv all.xml.tsv {WORKDIR}/{output.tsv}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   
     """

#{WORKDIR}/{input.protein}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   
##################################################################################                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##                                                                               #                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##                            FETCH SIMILAR GENES                                #                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##                                                                               #                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
##################################################################################                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           

rule blastSwissProt:
     input: protein="{genome}.faa", swissprot=UNIREF
     output: "{genome}.blastSwissProt"
     params: cluster="-cwd -V -l mem_free=8G -l h_vmem=8G"
     threads: THREADS
     shell:"""                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               
     blastp -db {input.swissprot} -query {input.protein} -num_threads {threads} -outfmt "7 qseqid qlen qstart qend sseqid slen sstart send pident nident evalue" > {output}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  
     """

